not_quite_lisp <- function(parentheses) {
    counter <- 1L
    floor <- 0L
    while (substr(parentheses, counter, counter) != "") {
      current_parenthesis <- substr(parentheses, counter, counter)
      if (floor == -1) {
        return(counter - 1)
      } else if (current_parenthesis == "(") {
          floor <- floor + 1
      } else {
          floor <- floor - 1
      }
      counter <- counter + 1
    }
    floor
}

